RETURNS TRIGGER
LANGUAGE plpgsql
SECURITY DEFINER
SET search_path = public
AS $$
DECLARE
  user_role TEXT;
BEGIN
  -- Get the role from the users table
  SELECT role INTO user_role
  FROM public.users
  WHERE id = NEW.id;

  -- Update the user's app_metadata with their role
  IF user_role IS NOT NULL THEN
