params.query:
-- 1. Add INSERT policy for users table
-- This allows authenticated users to insert their own profile
CREATE POLICY "Users can insert own profile"
ON users
FOR INSERT
TO authenticated
WITH CHECK (auth.uid() = id);

-- 2. Create a function to handle new user creation
CREATE OR REPLACE FUNCTION public.handle_new_user()
RETURNS TRIGGER AS $$
BEGIN
  INSERT INTO public.users (id, name, email, phone, role)
  VALUES (
    NEW.id,
    COALESCE(NEW.raw_user_meta_data->>'name', ''),
    NEW.email,
    COALESCE(NEW.raw_user_meta_data->>'phone', ''),
    COALESCE(NEW.raw_user_meta_data->>'role', 'customer')
  );
  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- 3. Create a trigger that runs after a new user signs up
DROP TRIGGER IF EXISTS on_auth_user_created ON auth.users;
CREATE TRIGGER on_auth_user_created
  AFTER INSERT ON auth.users
  FOR EACH ROW EXECUTE FUNCTION public.handle_new_user();

-- 4. Grant necessary permissions
GRANT USAGE ON SCHEMA public TO authenticated;
GRANT ALL ON public.users TO authenticated;

-- Create cart table for shopping functionality
CREATE TABLE IF NOT EXISTS cart (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id uuid REFERENCES users(id) ON DELETE CASCADE,
  product_id uuid REFERENCES products(id) ON DELETE CASCADE,
  quantity integer NOT NULL DEFAULT 1,
  created_at timestamptz DEFAULT now(),
  UNIQUE(user_id, product_id)
);

-- Enable RLS on cart
ALTER TABLE cart ENABLE ROW LEVEL SECURITY;

-- Cart policies
CREATE POLICY "Users can view their own cart" ON cart
  FOR SELECT USING (user_id = auth.uid());

CREATE POLICY "Users can add to their cart" ON cart
  FOR INSERT WITH CHECK (user_id = auth.uid());

CREATE POLICY "Users can update their cart" ON cart
  FOR UPDATE USING (user_id = auth.uid());

CREATE POLICY "Users can delete from their cart" ON cart
  FOR DELETE USING (user_id = auth.uid());

CREATE POLICY "Admins can view all carts" ON cart
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM users WHERE users.id = auth.uid() AND users.role = 'admin'
    )
  );

-- Add admin_coupon_config table for coupon management
CREATE TABLE IF NOT EXISTS admin_coupon_config (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  coupon_text text NOT NULL,
  discount_value numeric NOT NULL,
  is_spin_wheel boolean DEFAULT false,
  is_active boolean DEFAULT true,
  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now()
);

-- Enable RLS on admin_coupon_config
ALTER TABLE admin_coupon_config ENABLE ROW LEVEL SECURITY;

-- Admin coupon config policies
CREATE POLICY "Admins can manage coupon configs" ON admin_coupon_config
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM users WHERE users.id = auth.uid() AND users.role = 'admin'
    )
  );

CREATE POLICY "Users can view active coupon configs" ON admin_coupon_config
  FOR SELECT USING (is_active = true);

-- Add barber table for appointment booking
CREATE TABLE IF NOT EXISTS barbers (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  name text NOT NULL,
  email text,
  phone text,
  available_days text[] DEFAULT ARRAY['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'],
  available_hours jsonb DEFAULT '{"start": "09:00", "end": "18:00"}'::jsonb,
  is_active boolean DEFAULT true,
  created_at timestamptz DEFAULT now()
);

-- Enable RLS on barbers
ALTER TABLE barbers ENABLE ROW LEVEL SECURITY;

-- Barber policies
CREATE POLICY "Everyone can view active barbers" ON barbers
  FOR SELECT USING (is_active = true);

CREATE POLICY "Admins can manage barbers" ON barbers
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM users WHERE users.id = auth.uid() AND users.role = 'admin'
    )
  );

-- Add barber_id to appointments table
ALTER TABLE appointments ADD COLUMN IF NOT EXISTS barber_id uuid REFERENCES barbers(id);

-- Add coupon_id to coupons table for linking to admin config
ALTER TABLE coupons ADD COLUMN IF NOT EXISTS config_id uuid REFERENCES admin_coupon_config(id);
ALTER TABLE coupons ADD COLUMN IF NOT EXISTS coupon_code text;

